import React from "react";
import "./index.scss";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import {
  getDataProduct,
  getProductFilter,
  getDetailProduct,
  getDetailShop,
  getProductToCart,
} from "../../redux/action";

class CartPage extends React.Component {
  state = {
    totalP: 0,
  };
  handleTotal = () => {
    let totalP = 0;
    for (let i = 0; i < this.props.cartProducts.length; i++) {
      const ele_Price = this.props.cartProducts[i];
      totalP = ele_Price + totalP;
    }
    console.log(totalP);
  };
  render() {
    return (
      <div className="CartPage">
        <table>
          <tr>
            <th>Firstname</th>
            <th>Lastname</th>
            <th>total</th>
          </tr>
          {this.props.cartProducts.map((ele, index) => (
            <tr key={index}>
              <td>{ele.price}</td>
              <td>Smith</td>
              <td>50</td>
            </tr>
          ))}
        </table>
        <div onChange={this.handleTotal()}>{this.state.totalP}</div>
      </div>
    );
  }
}

const mapsStateToProps = (state) => ({
  productFlashSale: state.productFlashSale,
  productRecommend: state.productRecommend,
  shopSenMall: state.shopSenMall,
  productTopKeyWord: state.productTopKeyWord,
  productFilter: state.productFilter,
  banner: state.banner,
  menu: state.menu,
  sitemap: state.sitemap,
  themeEvent: state.themeEvent,
  detailProduct: state.detailProduct,
  detailShop: state.detailShop,
  cartProducts: state.cartProducts,
});

const mapDispatchToProps = (dispatch) => ({
  ...bindActionCreators(
    {
      getDataProduct,
      getProductFilter,
      getDetailProduct,
      getDetailShop,
      getProductToCart,
    },
    dispatch
  ),
});
export default connect(mapsStateToProps, mapDispatchToProps)(CartPage);
