import React from "react";
import "./index.scss";
import queryString from "query-string";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import {
  getDataProduct,
  getProductFilter,
  getDetailProduct,
  getDetailShop,
} from "../../redux/action";
import ProductRelated from "../../Atoms/ProductRelated";

class DetailPage extends React.Component {
  state = {
    quanity: 1,
    indexThumbnail: 0,
  };
  componentDidMount() {
    const parsed = queryString.parse(window.location.search);
    const id = parsed.id;
    const adminid = parsed.adminid;
    this.props.getDetailProduct(id);
    this.props.getDetailShop(adminid);
  }

  handleThumbnail = (i) => {
    this.setState({ indexThumbnail: i });
  };
  handleChose = (i, b) => {
    const ele_ByName = document.getElementsByName(`value${i}`);
    for (let i = 0; i < ele_ByName.length; i++) {
