import React from "react";
import "./index.scss";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import {
  getDataProduct,
  getProductFilter,
  getArrayFilter,
} from "../../redux/action";

class FilterPage extends React.Component {
  state = {
    path: `&p=1&platform=web&q=${this.props.query}&s=30&search_algo=algo6&sortType=rank`,
  };
  componentDidMount() {
    this.props.getArrayFilter(this.props.query);
  }
  handleCheckBox_PositionTop = (a, b, index, length) => {
    console.log(length);
    const eleId = document.getElementById(`PositionTop${index}`);
    console.log(eleId.checked);
    let c = `${b}`;
    let d = a[c];
    let e = `${b}=${d}`;
    console.log(e, "ra ko ");
    this.setState(
      {
        path: `&${e}${this.state.path}`,
      },
      () => {
        console.log(this.state.path, "path");
        this.props.getProductFilter(this.state.path);
      }
    );
  };
  handleCheckBox_GeneralTerm = (a) => {
    console.log(a, "aaaa");
  };
  handleCheckBox_DefaultTerm = (b, d) => {
    console.log(b, "bbbb");
    console.log(d, "dddd");
  };
  render() {
    return (
      <div className="FilterPage-wraper">
        <div className="FilterPage-wraper-left">
          {this.props.defaultTerm && (
            <div
              className="FilterPage-wraper-left-container-DefaultTerm"
              id="accordion"
            >
              {this.props.generalTerm && (
                <div className="DefaultTerm">
                  <div className="card">
                    <div className="card-header DefaultTerm-header">
                      <a
                        className="card-link DefaultTerm-header-link"
                        data-toggle="collapse"
                        href="#general"
                      >
                        BỘ LỌC SẢN PHẨM
                      </a>
                    </div>
                    <div id="general" className="collapse show">
                      <div className="card-body DefaultTerm-header-body">
                        {this.props.generalTerm.map((ele, index) => (
                          <div className="form-check-inline" key={index}>
                            <label className="form-check-label">
                              <input
                                type="checkbox"
                                className="form-check-input"
                                id={ele.attribute_value}
                                onClick={() =>
                                  this.handleCheckBox_GeneralTerm()
                                }
                              />
                              {ele.attribute_name}
                            </label>
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>
                </div>
              )}
              {this.props.defaultTerm.map((ele, indexa) => (
                <div
                  className="DefaultTerm"
                  key={`a${indexa}`}
                  attributeKeyIndex={indexa}
                >
                  <div className="card">
                    <div className="card-header DefaultTerm-header">
                      <a
                        className="card-link DefaultTerm-header-link"
                        data-toggle="collapse"
                        href={`#collapse${indexa}`}
                      >
                        {ele.attribute_name}
                      </a>
                    </div>
                    <div id={`collapse${indexa}`} className="collapse show">
                      <div className="card-body DefaultTerm-header-body">
                        {this.props.defaultTerm[indexa].attribute_value.map(
                          (ele, indexb) => (
                            <div className="form-check-inline" key={indexb}>
                              <label className="form-check-label">
                                <input
                                  type="checkbox"
                                  className="form-check-input"
                                  onClick={() =>
                                    this.handleCheckBox_DefaultTerm(
                                      ele.option_id,
                                      this.props.defaultTerm[indexa]
                                        .attribute_key
                                    )
                                  }
                                />
                                {ele.option_name}
                              </label>
                            </div>
                          )
                        )}
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
        <div className="FilterPage-wraper-right">
          {this.props.generalTerm_PositionTop && (
            <div className="FilterPage-wraper-right-container-PositionTop">
              {this.props.generalTerm_PositionTop.map((ele, index) =>
                index === 0 ? (
                  <div className="form-check-inline PositionTop" key={index}>
                    <label className="form-check-label">
                      <input
                        type="checkbox"
                        id={`PositionTop${index}`}
                        className="form-check-input"
                        onClick={() =>
                          this.handleCheckBox_PositionTop(
                            ele.attribute_value,
                            ele.attribute_key,
                            index,
                            this.props.generalTerm_PositionTop.length
                          )
                        }
                      />
                      <img src={ele.attribute_img} alt="isEvent" />
                    </label>
                  </div>
                ) : (
                  <div className="form-check-inline PositionTop" key={index}>
                    <label className="form-check-label">
                      <input
                        type="checkbox"
                        id={`PositionTop${index}`}
                        className="form-check-input"
                        onClick={() =>
                          this.handleCheckBox_PositionTop(
                            ele.attribute_value,
                            ele.attribute_key,
                            index
                          )
                        }
                      />
                      {ele.attribute_name}
                    </label>
                  </div>
                )
              )}
            </div>
          )}
        </div>
      </div>
    );
  }
}

const mapsStateToProps = (state) => ({
  productFilter: state.productFilter,
  defaultTerm: state.defaultTerm,
  generalTerm: state.generalTerm,
  generalTerm_PositionTop: state.generalTerm_PositionTop,
  query: state.query,
});

const mapDispatchToProps = (dispatch) => ({
  ...bindActionCreators(
    {
      getDataProduct,
      getProductFilter,
      getArrayFilter,
    },
    dispatch
  ),
});
export default connect(mapsStateToProps, mapDispatchToProps)(FilterPage);
