  // FireBase
  // Login Firebase
  loginFireBase = () => {
    let email = document.getElementById("login_Email").value;
    let password = document.getElementById("login_Password").value;
    // Login Function
    this.clearErrors();
    this.clearInput();
    firebaseApp
      .auth()
      .signInWithEmailAndPassword(email, password)
      .then(() => {
        this.setState({ ...this.state, isLoggedSuccess: true, isLogged: true });
        // firebaseApp.auth().onAuthStateChanged();
        let user = firebaseApp.auth().currentUser;
        console.log(user, "user");
        //set name and photo url
        this.setState({
          ...this.state,
          name: user.displayName,
          photoUrl: user.photoURL,
        });
        this.props.getUserLogged(user.displayName, true);
      })
      .catch(function (error) {
        // Handle Errors here.
        var errorCode = error.code;
        // document.getElementById("errorLogin").innerHTML = errorCode;
        switch (errorCode) {
          case "auth/invalid-email":
            document.getElementById("login_Error").innerHTML =
              "Email phải đúng định dạng";
            break;
          case "auth/wrong-password":
            document.getElementById("login_Error").innerHTML =
              "Bạn đã điền sai Password";
            break;
          case "auth/user-not-found":
            document.getElementById("login_Error").innerHTML =
              "Email này chưa được đăng ký";
            break;
          default:
            break;
        }
      });
  };
  // Logout FireBase
  logoutFireBase = () => {
    document.querySelector(".modal-wraper").style.display = "none";
    document.getElementById("root").style.position = "unset";
    document.getElementById("root").style.width = "100%";
    firebaseApp
      .auth()
      .signOut()
      .then(() =>
        this.setState({
          ...this.state,
          isLogged: false,
          photo: "",
          photoUrl: "",
          name: "",
        })
      );
  };

  // Register FireBase
  registerFireBase = () => {
    let email = document.getElementById("register_Email").value;
    let password = document.getElementById("register_Password").value;
    let name = document.getElementById("register_Name").value;

    this.clearInput();
    this.clearErrors();
    firebaseApp
      .auth()
      .createUserWithEmailAndPassword(email, password)
      .then(() => {
        //Khai bai User da dang nhap FireBase
        let user = firebaseApp.auth().currentUser;

        //Upload avatart len FireBase de lay URL
        const uploadTask = storage
          .ref(`images/${this.state.photo.name}`)
          .put(this.state.photo);

        uploadTask.on(
          "state_changed",
          (snapshot) => {},
          (error) => {
            console.log(error);
          },
          () => {
            storage
              .ref("images")
              .child(this.state.photo.name)
              .getDownloadURL()
              .then((url) => {
                this.setState({ ...this.state, photoUrl: url, name: name });
                console.log(this.state.photoUrl);
              })
              .then(() => {
                //Cap nhat vao provide User
                user
                  .updateProfile({
                    displayName: name,
                    photoURL: this.state.photoUrl,
                  })
                  .then(function (res) {
                    // Update successful.
                    console.log(res, "resss");
                  })
                  .catch(function (error) {
                    // An error happened.
                    console.log(error, "eee");
                  });
              });
          }
        );
      })
      .then(() =>
        this.setState({
          ...this.state,
          isLogged: true,
          isLoggedSuccess: true,
        })
      )
      .catch(function (error) {
        // Handle Errors here.
        var errorCode = error.code;
        // document.getElementById("errorLogin").innerHTML = errorCode;
        switch (errorCode) {
          case "auth/email-already-in-use":
            document.getElementById("register_Error").innerHTML =
              "Email đã được đăng ký";
            break;
          case "auth/invalid-email":
            document.getElementById("register_Error").innerHTML =
              "Email phải đúng định dạng";
            break;
          case "auth/weak-password":
            document.getElementById("register_Error").innerHTML =
