import React from "react";
import "./index.scss";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import {
  getDataProduct,
  getProductFilter,
  getArrayFilter,
  getPath_Default,
  change_isUpdate,
  getStatusFilter,
  handleSort,
} from "../../redux/action";
import ProductCardFILTER from "../../Atoms/ProductCardFILTER";

class FilterPage extends React.Component {
  state = {
    pathPositionTop: "",
    pathGeneral: "",
    pathDefault: "",
    sortType: "sortType=rank",
    page: 1,
  };

  componentDidMount() {
    // this.props.getArrayFilter(this.props.query);
    this.props.getProductFilter(this.props.query);
    // this.props.getStatusFilter("startLoading");
  }

  componentDidUpdate() {
    if (this.props.isUpdate) {
      console.log("isupdate");
      this.props.getProductFilter(
        this.state.pathPositionTop,
        this.state.pathDefault,
        this.state.pathGeneral,
        this.props.query,
        "96",
        this.state.sortType
      );
      const elArr = document.getElementsByName("page-item");
      console.log(elArr, "aaa");
      for (let i = 0; i < elArr.length; i++) {
        const eleId = document.getElementById(`page-item-${i + 1}`);
        eleId.classList.remove("active");
      }
      // document.getElementsByName("page-item").classList.remove("active");
      document
        .getElementById(`page-item-${this.state.page}`)
        .classList.add("active");
      this.props.change_isUpdate(false);
    }
  }
  //render
  render() {
    return (
      <div className="FilterPage-wraper">
        <div className="productFilter">
          <div className="productFilter-wraper">
            <div className="productFilter-title">
              <p>KẾT QUẢ TÌM KIẾM</p>
              <div>
                <select
                  id="sort"
                  onClick={(e) => this.props.handleSort(e.target.value)}
                >
                  <option value="sapxeptheo">Sắp xếp theo</option>
                  <option
                    value="thapdencao"
                    onClick={(e) => this.props.handleSort(e.target.value)}
                  >
                    Giá thấp đến cao
                  </option>
                  <option
                    value="caodenthap"
                    onClick={(e) => this.props.handleSort(e.target.value)}
                  >
                    Giá cao đến thấp
                  </option>
                  <option
                    value="muanhieunhat"
                    onClick={(e) => this.props.handleSort(e.target.value)}
                  >
                    Mua nhiều nhất
                  </option>
                  <option
                    value="dangkhuyenmai"
                    onClick={(e) => this.props.handleSort(e.target.value)}
                  >
                    Đang khuyến mãi
                  </option>
                </select>
              </div>
            </div>
            {this.props.statusFilter === "startLoading" && (
              <div className="loadingImg">
                <img
                  src="https://raw.githubusercontent.com/khacduy91/DuyVuCV/gh-pages/newgif.gif"
                  alt="loading"
                ></img>
              </div>
            )}
            {Object.keys(this.props.productFilter).length > 0 && (
              <div
                className="productFilter-container"
                id="productFilter-container"
              >
                {this.props.productFilter.map((ele, index) => (
                  <ProductCardFILTER product={ele} key={index} index={index} />
                ))}
              </div>
            )}
            <div> 1 2 3</div>
          </div>
        </div>
      </div>
    );
  }
}

const mapsStateToProps = (state) => ({
  productFilter: state.productFilter,
  defaultTerm: state.defaultTerm,
  generalTerm: state.generalTerm,
  generalTerm_PositionTop: state.generalTerm_PositionTop,
  query: state.query,
  pathDefault: state.pathDefault,
  errMsg: state.errMsg,
  isUpdate: state.isUpdate,
  statusFilter: state.statusFilter,
});

const mapDispatchToProps = (dispatch) => ({
  ...bindActionCreators(
    {
      getDataProduct,
      getProductFilter,
      getArrayFilter,
      getPath_Default,
      change_isUpdate,
      getStatusFilter,
      handleSort,
    },
    dispatch
  ),
});
export default connect(mapsStateToProps, mapDispatchToProps)(FilterPage);
