import React from "react";
import "./index.scss";
import queryString from "query-string";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import {
  getDataProduct,
  getProductFilter,
  getDetailProduct,
} from "../../redux/action";

class DetailPage extends React.Component {
  componentDidMount() {
    const parsed = queryString.parse(window.location.search);
    const id = parsed.id;
    this.props.getDetailProduct(id);
  }
  render() {
    return (
      <>
        {Object.keys(this.props.detailProduct).length > 0 && (
          <div className="detailPage-ProductInfo">
            <div className="detailPage-ProductInfo-Gallery">
              {this.props.detailProduct.data.images.map((ele, index) => (
                <div className="detailPage-ProductInfo-Gallery-image">
                  <img src={`https://media3.scdn.vn${ele}`} alt={`${index}`} />
                </div>
              ))}
            </div>
            <div className="detailPage-ProductInfo-BasicInfo">
              <div className="detailPage-ProductInfo-BasicInfo-row-title">
                <p>
                  {this.props.detailProduct.data.name}-
                  {this.props.detailProduct.data.sku_user}
                </p>
              </div>
              <div className="detailPage-ProductInfo-BasicInfo-row priceBox">
                <div>
                  <p>{this.props.detailProduct.data.final_price}</p>
                  <p>{this.props.detailProduct.data.price}</p>
                  <p>{this.props.detailProduct.data.final_promotion}</p>
                </div>
                <div>
                  <p>{this.props.detailProduct.data.rating_info.total_rat}</p>
                  <p>
                    {this.props.detailProduct.data.rating_info.percent_star}
                  </p>
                </div>
                <div></div>
              </div>
              <div className="detailPage-ProductInfo-BasicInfo-row freeShip"></div>
              <div className="detailPage-ProductInfo-BasicInfo-row instant"></div>
              <div className="detailPage-ProductInfo-BasicInfo-row attribute"></div>
              <div className="detailPage-ProductInfo-BasicInfo-row addToCart"></div>
              <div className="detailPage-ProductInfo-BasicInfo-row benefit"></div>
              <div className="detailPage-ProductInfo-BasicInfo-row benefit"></div>
            </div>
          </div>
        )}
      </>
    );
  }
}

const mapsStateToProps = (state) => ({
  productFlashSale: state.productFlashSale,
  productRecommend: state.productRecommend,
  shopSenMall: state.shopSenMall,
  productTopKeyWord: state.productTopKeyWord,
  productFilter: state.productFilter,
  banner: state.banner,
  menu: state.menu,
  sitemap: state.sitemap,
  themeEvent: state.themeEvent,
  detailProduct: state.detailProduct,
});

const mapDispatchToProps = (dispatch) => ({
  ...bindActionCreators(
    {
      getDataProduct,
      getProductFilter,
      getDetailProduct,
    },
    dispatch
  ),
});
export default connect(mapsStateToProps, mapDispatchToProps)(DetailPage);
