import React from "react";
import "./index.scss";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import {
  getDataProduct,
  getProductFilter,
  getDetailProduct,
  getDetailShop,
  getProductToCart,
  deleteProductCart,
} from "../../redux/action";

class CartPage extends React.Component {
  state = {
    totalP: 0,
  };
  componentDidMount() {
    let totalP = 0;
    for (let i = 0; i < this.props.cartProducts.length; i++) {
      const ele_Price = this.props.cartProducts[i].price;
      totalP = ele_Price * 1 + totalP;
    }

    this.setState({ totalP: totalP }, () =>
      console.log(this.state.totalP, "s")
    );
  }
  handleDelete = (i) => {
    this.props.cartProducts.splice(i, 1);
  };
  render() {
    return (
      <div className="CartPage">
        <table>
          <tr>
            <th>Firstname</th>
            <th>Lastname</th>
            <th>total</th>
          </tr>
          {this.props.cartProducts.map((ele, index) => (
            <tr key={index}>
              <td>{ele.price}</td>
              <td>Smith</td>
              <td>
                <button onClick={() => this.handleDelete(index)}>xoa</button>
              </td>
            </tr>
          ))}
        </table>
        <div onChange={() => this.handleTotal()}>{this.state.totalP}</div>
      </div>
    );
  }
}

const mapsStateToProps = (state) => ({
  productFlashSale: state.productFlashSale,
  productRecommend: state.productRecommend,
  shopSenMall: state.shopSenMall,
  productTopKeyWord: state.productTopKeyWord,
  productFilter: state.productFilter,
  banner: state.banner,
  menu: state.menu,
  sitemap: state.sitemap,
  themeEvent: state.themeEvent,
  detailProduct: state.detailProduct,
  detailShop: state.detailShop,
  cartProducts: state.cartProducts,
});

const mapDispatchToProps = (dispatch) => ({
  ...bindActionCreators(
    {
      getDataProduct,
      getProductFilter,
      getDetailProduct,
      getDetailShop,
      getProductToCart,
      deleteProductCart,
    },
    dispatch
  ),
});
export default connect(mapsStateToProps, mapDispatchToProps)(CartPage);
